import { useState } from "react";

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: ""
  });

  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    setStatus("Sending...");

    try {
      const response = await fetch("https://script.google.com/macros/s/AKfycbz4Q-5oGG-Ooca8SErL8qylwRFZC-B4fZAaaqL18J0LoFgzuLybaUI5U2Z8zBynjIdz/exec", {
        method: "POST",
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setStatus("✅ Message sent successfully!");
        setFormData({ name: "", email: "", message: "" });
      } else {
        setStatus("❌ Failed to send. Try again!");
      }
    } catch (error) {
      setStatus("⚠️ Error: " + error.message);
    }
  };

  return (
    <section id="contact" className="py-16 px-6 text-center transition-colors duration-500">
      <h2 className="text-2xl font-bold mb-4">Contact Me</h2>

      <form 
        onSubmit={handleSubmit}
        className="flex flex-col gap-3 max-w-md mx-auto"
      >
        <input 
          type="text" 
          name="name" 
          placeholder="Your Name" 
          value={formData.name}
          onChange={handleChange}
          className="p-2 border rounded bg-white text-black dark:bg-gray-800 dark:text-white transition-colors duration-500" 
          required 
        />
        <input 
          type="email" 
          name="email" 
          placeholder="Your Email" 
          value={formData.email}
          onChange={handleChange}
          className="p-2 border rounded bg-white text-black dark:bg-gray-800 dark:text-white transition-colors duration-500" 
          required 
        />
        <textarea 
          name="message" 
          placeholder="Your Message" 
          value={formData.message}
          onChange={handleChange}
          className="p-2 border rounded bg-white text-black dark:bg-gray-800 dark:text-white transition-colors duration-500"
          required
        />

        <button 
          type="submit" 
          className="bg-yellow-500 text-white px-4 py-2 rounded shadow hover:bg-yellow-600 transition-colors duration-300"
        >
          Send
        </button>
      </form>

      <p className="mt-4">{status}</p>

      <p className="mt-2">
        Or email me at{" "}
        <a href="mailto:yashasvi.khatri05@gmail.com" className="text-yellow-500">
          yashasvi.khatri05@gmail.com
        </a>
      </p>
    </section>
  );
}
